mapper.select:
select full.*, count(*) over() as user_cnt from (select distinct u.*,
ifnull(user_reported_cnt,0) as user_reported_cnt,
ifnull(cnt1,0) + ifnull(cnt2,0) as user_deal_cnt from user u left outer join (select deal.user_email_buyer, count(*) over(partition by deal.user_email_buyer) as cnt1 from deal) d1 on u.user_email = d1.user_email_buyer left outer join (select deal.user_email_seller, count(*) over(partition by deal.user_email_seller) as cnt2 from deal) d2 on u.user_email = d2.user_email_seller
left outer join (select report_receiver, count(*) over(partition by report_receiver) as user_reported_cnt from report) r on u.user_email = r.report_receiver
) full
and user_reported_cnt > 0
and user_reported_cnt between #{startReportedCnt} and #{endReportedCnt}
and user_reported_cnt <= #{endReportedCnt}
and user_reported_cnt >= #{startReportedCnt}
and user_score between #{startScore} and #{endScore}
and user_score <= #{endScore}
and user_score >= #{startScore}
and user_deal_cnt between #{startDealCnt} and #{endDealCnt}
and user_deal_cnt < #{endDealCnt} + 1
and user_deal_cnt > #{startDealCnt} - 1
and user_review_cnt between #{startReviewCnt} and #{endDealCnt}
and user_review_cnt < #{endReviewCnt} + 1
and user_review_cnt > #{startReviewCnt} - 1
and user_email like CONCAT('%',#{keyword},'%')
and user_nickname like CONCAT('%',#{keyword},'%')
and user_phnum like CONCAT('%',#{keyword},'%')
order by user_email
order by user_nickname
order by user_deal_cnt desc
order by user_review_cnt desc
order by user_reported_cnt desc
limit #{begin},#{listSize}

select *, count(*) over() as report_cnt, rt.report_type_name from report r inner join report_type rt on r.report_type_code = rt.report_type_code
report_receiver = #{keyword}
auction_no = #{keyword}
limit #{begin},5

select a.*, count(*) over() as auction_cnt, ifnull(bid_price,0) as bid_price,
ifnull(auction_reported_cnt,0) as auction_reported_cnt,
c.category_name
from auction a left outer join (select distinct max(bid_price) over(partition by auction_no) as bid_price, auction_no from bid ) d on a.auction_no = d.auction_no inner join category c on a.category_code = c.category_code
left outer join (select auction_no, count(*) as auction_reported_cnt from report group by auction_no) r on a.auction_no= r.auction_no
and auction_reported_cnt > 0
and auction_reported_cnt between #{startReportedCnt} and #{endReportedCnt}
and auction_reported_cnt <= #{endReportedCnt}
and auction_reported_cnt >= #{startReportedCnt}
and bid_price between #{startPrice} and #{endPrice}
and bid_price <= #{endPrice}
and bid_price >= #{startPrice}
and auction_limit_date between #{startLimitDate} and #{endLimitDate}
and auction_limit_date <= #{endLimitDate}
and auction_limit_date >= #{startLimitDate}
and a.category_code in
#{staff}
and a.auction_no like CONCAT('%',#{keyword},'%')
and a.auction_title like CONCAT('%',#{keyword},'%')
and a.user_email like CONCAT('%',#{keyword},'%')
order by a.auction_no
order by user_email
order by bid_price desc
order by a.auction_limit_date
order by r.auction_reported_cnt desc
limit #{begin},#{listSize}

mapper.update:
update category
   set category_name = #{categoryName},
       category_order= #{categoryOrder},
   	   category_display = #{categoryDisplay}
where category_code = #{categoryCode}

update report_type
   set report_type_name = #{reportTypeName},
       report_type_order = #{reportTypeOrder}
where report_type_code = #{reportTypeCode}

mapper.select:
select d.*, a.auction_title, count(*) over() as deal_cnt from deal d inner join auction a on d.auction_no = a.auction_no
where d.deal_condition in
#{staff}
and a.auction_title like CONCAT('%',#{keyword},'%')
and d.user_email_buyer like CONCAT('%',#{keyword},'%')
and d.user_email_Seller like CONCAT('%',#{keyword},'%')
and d.deal_no between #{startDealNo} and #{endDealNo}
and d.deal_no <== #{endDealNo}
and d.deal_no >= #{startDealNo}
