mapper.update:
	update chat
   		set reads_buyer = 0, reads_seller = 0
	 where chat_no = #{chatNo}

mapper.select:
		select (select sum(reads_seller)
		  from chat
		 where user_email_seller = #{email}
		) as readsSeller,
        (select sum(reads_buyer)
		   from chat
		  where user_email_buyer = #{email}
		) as readsBuyer
		  from chat limit 1

mapper.update:
update conversation
   set covst_reads = 0
 where chat_no = #{chatNo}
   and user_Email != #{userEmail}
